#include <stdio.h>
#include <string.h>

int main() {
    char s[100001];
    int q;
    scanf("%s%d", s, &q);
    int i;
    for (i = 0; i < q; i++) {
        int t, f;
        char c;
        scanf("%d", &t);
        if (t == 1) {
            int l = 0, r = strlen(s) - 1;
            while (l < r) {
                char temp = s[l];
                s[l] = s[r];
                s[r] = temp;
                l++;
                r--;
            }
        } else {
            scanf("%d %c", &f, &c);
            if (f == 1) {
                s[0] = c;
            } else {
                int len = strlen(s);
                s[len] = c;
                s[len + 1] = '\0';
            }
        }
    }
    printf("%s\n", s);
    return 0;
}
