#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_WORD_LENGTH 30
#define MAX_PAGE_NUMBER 1000
#define MAX_PAIRS 100

typedef struct {
    char word[MAX_WORD_LENGTH];
    int page_number;
} Pair;

int compare_pairs(const void *a, const void *b) {
    Pair *pair1 = (Pair *)a;
    Pair *pair2 = (Pair *)b;
    return strcmp(pair1->word, pair2->word);
}

int main() {
    Pair pairs[MAX_PAIRS];
    int num_pairs = 0;

    char word[MAX_WORD_LENGTH];
    int page_number;

    while (scanf("%s %d", word, &page_number) == 2) {
        if (num_pairs < MAX_PAIRS) {
            strcpy(pairs[num_pairs].word, word);
            pairs[num_pairs].page_number = page_number;
            num_pairs++;
        } else {
            printf("Too many pairs. Exiting...\n");
            break;
        }
    }

    qsort(pairs, num_pairs, sizeof(Pair), compare_pairs);

    for (int i = 0; i < num_pairs; i++) {
        printf("%s", pairs[i].word);
        printf("\n");

        for (int j = i + 1; j < num_pairs; j++) {
            if (strcmp(pairs[i].word, pairs[j].word) == 0) {
                printf("%d", pairs[j].page_number);
                if (j < num_pairs - 1 && strcmp(pairs[j].word, pairs[j + 1].word) == 0) {
                    printf(" ");
                } else {
                    printf("\n");
                }
                j++;
            }
        }
    }

    return 0;
}
