#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 998244353
#define MAXN 200005

typedef struct {
    int x, d;
} Robot;

int cmp(const void *a, const void *b) {
    Robot *x = (Robot *)a;
    Robot *y = (Robot *)b;
    return x->x - y->x;
}

int n;
Robot a[MAXN];
int f[MAXN];

int main() {
    scanf("%d", &n);
    for (int i = 1; i <= n; i++) {
        scanf("%d %d", &a[i].x, &a[i].d);
    }
    qsort(a + 1, n, sizeof(Robot), cmp);
    f[0] = 1;
    for (int i = 1; i <= n; i++) {
        int l = 1, r = i - 1;
        while (l < r) {
            int mid = (l + r + 1) >> 1;
            if (a[i].x + a[i].d >= a[mid].x) {
                l = mid;
            } else {
                r = mid - 1;
            }
        }
        for (int j = 0; j <= i - 1; j++) {
            if (a[i].x + a[i].d >= a[j].x) {
                f[i] = (f[i] + f[j]) % MOD;
            }
        }
        f[i] = (f[i] + MOD - f[r]) % MOD;
    }
    printf("%d\n", f[n]);
    return 0;
}
