#include <iostream>
#include <vector>
#include <algorithm>
#include <utility>

using namespace std;

const int MOD = 998244353;

struct Robot {
    int x, d;
};

bool cmp(Robot a, Robot b) {
    return a.x < b.x;
}

int main() {
    int n;
    cin >> n;

    vector<Robot> robots(n);
    for (int i = 0; i < n; i++) {
        cin >> robots[i].x >> robots[i].d;
    }

    sort(robots.begin(), robots.end(), cmp);

    vector<int> dp(n + 1);
    dp[0] = 1;

    for (int i = 0; i < n; i++) {
        int j = i;
        while (j < n && robots[j].x < robots[i].x + robots[i].d) {
            j++;
        }

        int k = i + 1;
        while (k < j && robots[k].x < robots[i].x + robots[i].d) {
            k++;
        }

        dp[j] = (dp[j] + dp[i]) % MOD;
        if (k < j) {
            dp[j] = (dp[j] + dp[k]) % MOD;
        }
    }

    int ans = 0;
    for (int i = 0; i <= n; i++) {
        ans = (ans + dp[i]) % MOD;
    }

    cout << ans << endl;

    return 0;
}
