import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 998244353;
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;
        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }
        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }
        int nextInt() {
            return Integer.parseInt(next());
        }
        long nextLong() {
            return Long.parseLong(next());
        }
    }
    static class Pair implements Comparable<Pair> {
        long x, d;
        public Pair(long x, long d) {
            this.x = x;
            this.d = d;
        }
        public int compareTo(Pair other) {
            return Long.compare(x, other.x);
        }
    }
    public static void main(String[] args) {
        FastReader sc = new FastReader();
        int n = sc.nextInt();
        Pair[] a = new Pair[n];
        for (int i = 0; i < n; i++) {
            a[i] = new Pair(sc.nextLong(), sc.nextLong());
        }
        Arrays.sort(a);
        long[] dp = new long[n + 1];
        dp[n] = 1;
        for (int i = n - 1; i >= 0; i--) {
            int j = i + 1;
            while (j < n && a[j].x < a[i].x + a[i].d) {
                j++;
            }
            long sum = 0;
            for (int k = i + 1; k <= j; k++) {
                sum = (sum + dp[k]) % MOD;
            }
            dp[i] = (sum + 1) % MOD;
        }
        long ans = 0;
        for (int i = 0; i <= n; i++) {
            ans = (ans + dp[i]) % MOD;
        }
        System.out.println(ans);
    }
}
