#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_N 3

int main() {
    int n, m;
    scanf("%d %d", &n, &m);

    int s[MAX_N], c[MAX_N];
    for (int i = 0; i < m; i++) {
        scanf("%d %d", &s[i], &c[i]);
    }

    char num[MAX_N + 1];
    for (int i = 0; i < n; i++) {
        num[i] = '0';
    }
    num[n] = '\0';

    for (int i = 0; i < m; i++) {
        if (s[i] == 1) {
            num[0] = c[i] + '0';
        } else {
            num[n - s[i] + 1] = c[i] + '0';
        }
    }

    int len = strlen(num);
    if (num[0] == '0') {
        for (int i = 1; i < len; i++) {
            num[i - 1] = num[i];
        }
        num[len - 1] = '\0';
        len--;
    }

    if (len == n) {
        printf("%s\n", num);
    } else {
        printf("-1\n");
    }

    return 0;
}
