#include <iostream>
#include <string>
#include <vector>

int main() {
    int n, m;
    std::cin >> n >> m;

    std::vector<int> s(m);
    std::vector<int> c(m);

    for (int i = 0; i < m; i++) {
        std::cin >> s[i] >> c[i];
    }

    std::string ans = "";
    for (int i = 0; i < n; i++) {
        bool found = false;
        for (int j = 0; j < m; j++) {
            if (s[j] == i + 1) {
                ans += std::to_string(c[j]);
                found = true;
                break;
            }
        }
        if (!found) {
            std::cout << -1 << std::endl;
            return 0;
        }
    }

    for (int i = 0; i < n; i++) {
        if (i == n - 1) {
            std::cout << ans << std::endl;
        } else {
            std::cout << ans << "-";
        }
    }

    return 0;
}
