#include <stdio.h>
#include <stdlib.h>

#define N 100000
#define M 100000
#define K 100000

int main() {
    int n, m, k;
    int a[M], b[M], c[K], d[K];
    int adj[N][N], vis[N], dist[N];
    int queue[N], head, tail;

    scanf("%d %d %d", &n, &m, &k);
    for (int i = 0; i < m; i++) {
        scanf("%d %d", &a[i], &b[i]);
        a[i]--; b[i]--;
        adj[a[i]][b[i]] = 1;
        adj[b[i]][a[i]] = 1;
    }
    for (int i = 0; i < k; i++) {
        scanf("%d %d", &c[i], &d[i]);
        c[i]--; d[i]--;
        adj[c[i]][d[i]] = 2;
        adj[d[i]][c[i]] = 2;
    }

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (adj[i][j] == 1) {
                dist[i] = 1;
                break;
            }
        }
    }

    for (int i = 0; i < n; i++) {
        if (dist[i] == 0) {
            head = tail = 0;
            queue[tail++] = i;
            while (head < tail) {
                int u = queue[head++];
                for (int v = 0; v < n; v++) {
                    if (adj[u][v] == 1 && dist[v] == 0) {
                        dist[v] = dist[u] + 1;
                        queue[tail++] = v;
                    }
                }
            }
        }
    }

    for (int i = 0; i < n; i++) {
        int cnt = 0;
        for (int j = 0; j < n; j++) {
            if (adj[i][j] == 2) {
                cnt++;
            }
        }
        for (int j = 0; j < n; j++) {
            if (adj[i][j] == 1) {
                cnt++;
            }
        }
        for (int j = 0; j < n; j++) {
            if (i != j && adj[i][j] == 0 && dist[i] != 0 && dist[j] != 0 && dist[i] + dist[j] == dist[i] + dist[j]) {
                cnt++;
            }
        }
        printf("%d ", n - cnt - 1);
    }
    printf("\n");
    return 0;
}
