#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int MAX_N = 100000;

int main() {
    int N, M, K;
    cin >> N >> M >> K;

    vector<vector<int>> graph(N);
    vector<vector<int>> block(N);

    for (int i = 0; i < M; i++) {
        int u, v;
        cin >> u >> v;
        u--; v--;
        graph[u].push_back(v);
        graph[v].push_back(u);
    }

    for (int i = 0; i < K; i++) {
        int u, v;
        cin >> u >> v;
        u--; v--;
        block[u].push_back(v);
        block[v].push_back(u);
    }

    vector<int> dist(N, -1);
    queue<int> q;

    for (int i = 0; i < N; i++) {
        if (dist[i] == -1) {
            q.push(i);
            dist[i] = 0;
        }

        while (!q.empty()) {
            int u = q.front();
            q.pop();

            for (int v : graph[u]) {
                if (dist[v] == -1) {
                    dist[v] = dist[u] + 1;
                    q.push(v);
                }
            }

            for (int v : block[u]) {
                if (dist[v] == -1) {
                    dist[v] = -2;
                    q.push(v);
                }
            }
        }
    }

    for (int i = 0; i < N; i++) {
        int count = 0;

        for (int j = 0; j < N; j++) {
            if (i != j && dist[i] != -2 && dist[i] != -1 && dist[j] != -2 && dist[j] != -1 && dist[i] < dist[j]) {
                count++;
            }
        }

        cout << count << " ";
    }

    cout << endl;

    return 0;
}
