import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.*;

public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }

        double nextDouble() {
            return Double.parseDouble(next());
        }

        String nextLine() {
            String str = "";
            try {
                str = br.readLine();
            } catch (IOException e) {
                e.printStackTrace();
            }
            return str;
        }
    }

    static class Pair {
        int first;
        int second;

        public Pair(int first, int second) {
            this.first = first;
            this.second = second;
        }
    }

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        PrintWriter out = new PrintWriter(System.out);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int k = sc.nextInt();
        List<List<Integer>> graph = new ArrayList<>();
        List<List<Integer>> block = new ArrayList<>();
        for (int i = 0; i <= n; i++) {
            graph.add(new ArrayList<>());
            block.add(new ArrayList<>());
        }
        for (int i = 0; i < m; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            graph.get(u).add(v);
            graph.get(v).add(u);
        }
        for (int i = 0; i < k; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            block.get(u).add(v);
            block.get(v).add(u);
        }
        int[] ans = new int[n + 1];
        for (int i = 1; i <= n; i++) {
            ans[i] = dfs(graph, block, i, i);
        }
        for (int i = 1; i <= n; i++) {
            out.print(ans[i] + " ");
        }
        out.println();
        out.close();
    }

    static int dfs(List<List<Integer>> graph, List<List<Integer>> block, int u, int prev) {
        if (graph.get(u).size() == 1 && graph.get(u).get(0) == prev) {
            return 0;
        }
        int cnt = 0;
        for (int v : graph.get(u)) {
            if (v != prev) {
                cnt += dfs(graph, block, v, u);
            }
        }
        for (int v : block.get(u)) {
            if (v != prev) {
                cnt = 0;
                break;
            }
        }
        return cnt;
    }
}
