import java.util.*;

public class BookIndex {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        Map<String, List<Integer>> bookIndex = new TreeMap<>();

        while (scanner.hasNextLine()) {
            String[] line = scanner.nextLine().split(" ");
            String word = line[0];
            int page = Integer.parseInt(line[1]);

            bookIndex.computeIfAbsent(word, k -> new ArrayList<>()).add(page);
        }

        for (Map.Entry<String, List<Integer>> entry : bookIndex.entrySet()) {
            System.out.println(entry.getKey());
            Collections.sort(entry.getValue());
            for (int page : entry.getValue()) {
                System.out.print(page + " ");
            }
            System.out.println();
        }
    }
}
