#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define N 500000
#define Q 20000

typedef struct {
    int l, r;
    int cnt;
} Node;

Node tree[4 * N + 5];

void update(int i, int l, int r, int pos, char c) {
    if (l == r) {
        if (tree[i].l != c) {
            tree[i].l = c;
            tree[i].cnt = 1;
        } else {
            tree[i].cnt++;
        }
        return;
    }
    int mid = (l + r) / 2;
    if (pos <= mid) {
        update(2 * i, l, mid, pos, c);
    } else {
        update(2 * i + 1, mid + 1, r, pos, c);
    }
    tree[i].cnt = tree[2 * i].cnt + tree[2 * i + 1].cnt;
}

int query(int i, int l, int r, int ql, int qr) {
    if (l >= ql && r <= qr) {
        return tree[i].cnt;
    }
    int mid = (l + r) / 2;
    int res = 0;
    if (ql <= mid) {
        res += query(2 * i, l, mid, ql, qr);
    }
    if (qr > mid) {
        res += query(2 * i + 1, mid + 1, r, ql, qr);
    }
    return res;
}

int main() {
    int n, q;
    char s[N + 5];
    scanf("%d %s %d", &n, s, &q);
    for (int i = 0; i < 4 * n + 5; i++) {
        tree[i].l = 0;
        tree[i].r = 0;
        tree[i].cnt = 0;
    }
    for (int i = 0; i < n; i++) {
        update(1, 0, n - 1, i, s[i]);
    }
    for (int i = 0; i < q; i++) {
        int type;
        scanf("%d", &type);
        if (type == 1) {
            int pos, c;
            scanf("%d %c", &pos, &c);
            pos--;
            update(1, 0, n - 1, pos, c);
        } else {
            int l, r;
            scanf("%d %d", &l, &r);
            l--;
            r--;
            printf("%d\n", query(1, 0, n - 1, l, r));
        }
    }
    return 0;
}
