#include <iostream>
#include <vector>
#include <string>
#include <set>
#include <algorithm>

using namespace std;

const int N = 5e5 + 5;

int n, q;
string s;
vector<int> tree[4 * N];

void build(int v, int l, int r) {
    if (l == r) {
        tree[v].push_back(0);
        return;
    }
    int m = (l + r) / 2;
    build(2 * v, l, m);
    build(2 * v + 1, m + 1, r);
    tree[v].insert(tree[v].end(), tree[2 * v].begin(), tree[2 * v].end());
    tree[v].insert(tree[v].end(), tree[2 * v + 1].begin(), tree[2 * v + 1].end());
}

void update(int v, int l, int r, int pos, int val) {
    if (l == r) {
        tree[v].push_back(val);
        return;
    }
    int m = (l + r) / 2;
    if (pos <= m) update(2 * v, l, m, pos, val);
    else update(2 * v + 1, m + 1, r, pos, val);
    tree[v].insert(tree[v].end(), tree[2 * v].begin(), tree[2 * v].end());
    tree[v].insert(tree[v].end(), tree[2 * v + 1].begin(), tree[2 * v + 1].end());
}

int query(int v, int l, int r, int ql, int qr) {
    if (ql <= l && r <= qr) {
        return tree[v].size();
    }
    int m = (l + r) / 2;
    int res = 0;
    if (ql <= m) res += query(2 * v, l, m, ql, qr);
    if (qr > m) res += query(2 * v + 1, m + 1, r, ql, qr);
    return res;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n >> s;
    build(1, 0, n - 1);
    cin >> q;
    for (int i = 0; i < q; ++i) {
        int type;
        cin >> type;
        if (type == 1) {
            int pos, c;
            char ch;
            cin >> pos >> ch;
            --pos;
            update(1, 0, n - 1, pos, s[pos] == ch ? 0 : 1);
            s[pos] = ch;
        } else {
            int l, r;
            cin >> l >> r;
            --l;
            cout << query(1, 0, n - 1, l, r) << '\n';
        }
    }

    return 0;
}
