class SegmentTree:
    def __init__(self, n):
        self.n = n
        self.tree = [[0, 0] for _ in range(4 * n)]

    def update(self, node, l, r, i, c):
        if l == r:
            self.tree[node][0] = 1
            self.tree[node][1] = c
            return
        mid = (l + r) // 2
        if i <= mid:
            self.update(2 * node, l, mid, i, c)
        else:
            self.update(2 * node + 1, mid + 1, r, i, c)
        self.tree[node][0] = self.tree[2 * node][0] + self.tree[2 * node + 1][0]
        self.tree[node][1] = max(self.tree[2 * node][1], self.tree[2 * node + 1][1])

    def query(self, node, l, r, ql, qr):
        if l >= ql and r <= qr:
            return self.tree[node][0], self.tree[node][1]
        mid = (l + r) // 2
        if qr <= mid:
            return self.query(2 * node, l, mid, ql, qr)
        elif ql > mid:
            return self.query(2 * node + 1, mid + 1, r, ql, qr)
        else:
            left, left_max = self.query(2 * node, l, mid, ql, qr)
            right, right_max = self.query(2 * node + 1, mid + 1, r, ql, qr)
            return left + right, max(left_max, right_max)

def solve():
    n = int(input())
    s = input()
    q = int(input())
    queries = []
    for _ in range(q):
        query = list(map(int, input().split()))
        queries.append(query)
    seg = SegmentTree(n)
    for i, c in enumerate(s):
        seg.update(1, 0, n - 1, i, c)
    for query in queries:
        if query[0] == 1:
            i, c = query[1] - 1, query[2]
            seg.update(1, 0, n - 1, i, c)
        else:
            l, r = query[1] - 1, query[2] - 1
            count, max_val = seg.query(1, 0, n - 1, l, r)
            print(count)

for _ in range(1):
    solve()
