#include <stdio.h>
#include <math.h>

typedef struct {
    int x, y, c;
} Meat;

int compare(const void *a, const void *b) {
    Meat *m1 = (Meat *)a;
    Meat *m2 = (Meat *)b;
    return (m1->c * sqrt(pow(m1->x, 2) + pow(m1->y, 2))) - (m2->c * sqrt(pow(m2->x, 2) + pow(m2->y, 2)));
}

int main() {
    int N, K;
    scanf("%d %d", &N, &K);
    Meat meats[N];
    for (int i = 0; i < N; i++) {
        scanf("%d %d %d", &meats[i].x, &meats[i].y, &meats[i].c);
    }
    qsort(meats, N, sizeof(Meat), compare);
    double min_time = 1e9;
    for (int i = 0; i < N; i++) {
        double x = meats[i].x;
        double y = meats[i].y;
        double time = 0;
        for (int j = 0; j < N; j++) {
            time += meats[j].c * sqrt(pow(x - meats[j].x, 2) + pow(y - meats[j].y, 2));
        }
        if (i + 1 >= K) {
            min_time = fmin(min_time, time);
        }
    }
    printf("%.6lf\n", min_time);
    return 0;
}
