#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

using namespace std;

struct Meat {
    int x, y, c;
};

bool compareMeat(Meat a, Meat b) {
    return a.c * sqrt(a.x * a.x + a.y * a.y) < b.c * sqrt(b.x * b.x + b.y * b.y);
}

double solve(vector<Meat>& meats, int k) {
    sort(meats.begin(), meats.end(), compareMeat);
    double ans = 1e9;
    for (int i = 0; i < meats.size(); i++) {
        double x = meats[i].x, y = meats[i].y;
        double time = 0;
        for (int j = 0; j < k; j++) {
            time = max(time, meats[j].c * sqrt((x - meats[j].x) * (x - meats[j].x) + (y - meats[j].y) * (y - meats[j].y)));
        }
        ans = min(ans, time);
    }
    return ans;
}

int main() {
    int n, k;
    cin >> n >> k;
    vector<Meat> meats(n);
    for (int i = 0; i < n; i++) {
        cin >> meats[i].x >> meats[i].y >> meats[i].c;
    }
    cout << fixed << setprecision(6) << solve(meats, k) << endl;
    return 0;
}
