#include <stdio.h>

int main() {
    int a;
    while (scanf("%d", &a) == 1) {
        if (a == 0) break;
        int min = 1000000000;
        for (int i = 0; i < 3; i++) {
            int num = a / 500;
            int price = 0;
            for (int j = 0; j < num; j++) {
                if (i == 0) {
                    price += 380;
                    if (j % 5 == 0 && j != 0) price *= 0.8;
                } else if (i == 1) {
                    price += 550;
                    if (j % 4 == 0 && j != 0) price *= 0.85;
                } else {
                    price += 850;
                    if (j % 3 == 0 && j != 0) price *= 0.88;
                }
            }
            price += (a % 500) * (i == 0 ? 380 : (i == 1 ? 550 : 850));
            min = (price < min) ? price : min;
        }
        printf("%d\n", min);
    }
    return 0;
}
