#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const long long MOD = 1e9 + 7;

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    long long a, b;
    cin >> n >> a >> b;

    if (a > b) swap(a, b);
    if (a == b) {
        cout << n - 2 << '\n';
        return 0;
    }

    vector<long long> fac(n + 1);
    fac[0] = 1;
    for (int i = 1; i <= n; i++) fac[i] = fac[i - 1] * i % MOD;

    long long res = fac[n] - fac[b - 1] - fac[a - 1];
    res = (res % MOD + MOD) % MOD;

    res = res * power(fac[b - a - 1], MOD - 2) % MOD;
    res = res * power(fac[n - b], MOD - 2) % MOD;
    res = res * power(fac[n - a - 1], MOD - 2) % MOD;

    cout << res << '\n';

    return 0;
}
