#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

long long choose(long long n, long long k) {
    long long result = 1;
    for (long long i = 1; i <= k; i++) {
        result = (result * (n - i + 1)) % MOD;
        result = (result * powMod(i, MOD - 2, MOD)) % MOD;
    }
    return result;
}

long long powMod(long long base, long long exponent, long long mod) {
    long long result = 1;
    while (exponent > 0) {
        if (exponent % 2 == 1) {
            result = (result * base) % mod;
        }
        base = (base * base) % mod;
        exponent /= 2;
    }
    return result;
}

int main() {
    long long n, k;
    scanf("%lld %lld", &n, &k);
    long long max = n * (n - 1) / 2;
    if (k > max) {
        printf("0");
        return 0;
    }
    long long result = 0;
    for (long long i = 0; i <= k; i++) {
        long long j = k - i;
        long long x = n - i - j;
        if (x < 0) {
            break;
        }
        result = (result + choose(n - 1, i) * choose(i, j) % MOD * choose(x, n - 1 - i - j)) % MOD;
    }
    printf("%lld", result);
    return 0;
}
