#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int k, q;
    cin >> k >> q;

    vector<long long> d(k);
    for (int i = 0; i < k; i++) {
        cin >> d[i];
    }

    vector<long long> n(q), x(q), m(q);
    for (int i = 0; i < q; i++) {
        cin >> n[i] >> x[i] >> m[i];
    }

    for (int i = 0; i < q; i++) {
        long long ans = 0;
        long long prev = x[i] % m[i];
        long long curr = x[i] % m[i];
        long long diff = d[(n[i] - 2) % k];

        for (int j = 1; j < n[i] - 1; j++) {
            curr = (curr + diff) % m[i];
            if (curr < prev) {
                ans++;
            }
            prev = curr;
            diff = d[(j - 1) % k];
        }

        cout << ans << endl;
    }

    return 0;
}
