def calculate_cost(amount):
    costs = {
        'A': {'unit_price': 380, 'discount': 20, 'discount_unit': 5},
        'B': {'unit_price': 550, 'discount': 15, 'discount_unit': 4},
        'C': {'unit_price': 850, 'discount': 12, 'discount_unit': 3}
    }

    min_cost = float('inf')

    for shop in costs:
        num_bags = amount // 200 if shop == 'A' else amount // 300 if shop == 'B' else amount // 500
        discounted_bags = num_bags // costs[shop]['discount_unit']
        remaining_bags = num_bags - discounted_bags

        cost = (costs[shop]['unit_price'] * (discounted_bags * costs[shop]['discount_unit'] + remaining_bags)) * (1 - costs[shop]['discount'] / 100)

        min_cost = min(min_cost, cost)

    return int(min_cost)

t = int(input())
for _ in range(t):
    amount = int(input())
    if amount == 0:
        break
    print(calculate_cost(amount))
