int ans, x, y, z;

void add(int i, int j) {
    e[i] = j;
    d[i] = 1;
}

void dfs(int i) {
    if (f[i]) return;
    f[i] = 1;
    for (int j = e[i]; j; j = e[i]) {
        if (d[j]) {
            d[j] = 0;
            dfs(j);
        }
    }
}

