#include <iostream>
#include <vector>
#include <algorithm>
#include <set>

using namespace std;

struct SegmentTree {
    vector<int> tree;
    int n;

    SegmentTree(int n) : n(n), tree(4 * n, 0) {}

    void update(int p, int value) {
        p += n;
        tree[p] = value;
        for (p /= 2; p; p /= 2) {
            tree[p] = tree[2 * p] + tree[2 * p + 1];
        }
    }

    int query(int l, int r) {
        int res = 0;
        for (l += n, r += n; l <= r; l /= 2, r /= 2) {
            if (l & 1) {
                res += tree[l++];
            }
            if (~r & 1) {
                res += tree[r--];
            }
        }
        return res;
    }
};

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;

    vector<int> a(n);
    vector<int> b(n);
    vector<int> l(m);
    vector<int> r(m);

    for (int i = 0; i < n; ++i) {
        cin >> a[i] >> b[i];
    }

    for (int i = 0; i < m; ++i) {
        cin >> l[i] >> r[i];
    }

    sort(a.begin(), a.end());
    sort(l.begin(), l.end());
    sort(r.begin(), r.end());

    vector<int> pos(n);
    for (int i = 0; i < n; ++i) {
        pos[i] = lower_bound(l.begin(), l.end(), a[i]) - l.begin();
    }

    SegmentTree tree(n);
    for (int i = 0; i < n; ++i) {
        tree.update(pos[i], b[i]);
    }

    set<int> active;
    for (int i = 0; i < n; ++i) {
        if (tree.query(0, pos[i])) {
            active.insert(i);
        }
    }

    vector<int> ans;
    for (int i = 0; i < m; ++i) {
        int p = lower_bound(l.begin(), l.end(), r[i]) - l.begin();
        int q = upper_bound(r.begin(), r.end(), r[i]) - r.begin() - 1;

        if (active.size() == tree.query(p, q)) {
            ans.push_back(i + 1);
            active.clear();
            for (int j = p; j <= q; ++j) {
                if (tree.query(j, j)) {
                    active.insert(j);
                }
            }
        }
    }

    if (active.empty()) {
        cout << ans.size() << '\n';
        for (int x : ans) {
            cout << x << ' ';
        }
    } else {
        cout << -1;
    }

    return 0;
}
