import java.io.*;
import java.util.*;

public class Main {
    static class SegmentTree {
        int n;
        int[] tree;
        int[] lazy;

        public SegmentTree(int n) {
            this.n = n;
            tree = new int[4 * n];
            lazy = new int[4 * n];
        }

        void push(int v, int l, int r) {
            if (lazy[v] != 0) {
                tree[v] = lazy[v] * (r - l + 1);
                if (l != r) {
                    lazy[2 * v] = lazy[v];
                    lazy[2 * v + 1] = lazy[v];
                }
                lazy[v] = 0;
            }
        }

        void update(int v, int l, int r, int a, int b, int val) {
            push(v, l, r);
            if (l > b || r < a) return;
            if (l >= a && r <= b) {
                lazy[v] = val;
                push(v, l, r);
                return;
            }
            int m = (l + r) / 2;
            update(2 * v, l, m, a, b, val);
            update(2 * v + 1, m + 1, r, a, b, val);
            tree[v] = tree[2 * v] + tree[2 * v + 1];
        }

        int query(int v, int l, int r, int a, int b) {
            push(v, l, r);
            if (l > b || r < a) return 0;
            if (l >= a && r <= b) return tree[v];
            int m = (l + r) / 2;
            return query(2 * v, l, m, a, b) + query(2 * v + 1, m + 1, r, a, b);
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        int m = Integer.parseInt(st.nextToken());
        int[] a = new int[n];
        int[] b = new int[n];
        int[] c = new int[m];
        int[] d = new int[m];
        for (int i = 0; i < n; i++) {
            st = new StringTokenizer(br.readLine());
            a[i] = Integer.parseInt(st.nextToken());
            b[i] = Integer.parseInt(st.nextToken());
        }
        for (int i = 0; i < m; i++) {
            st = new StringTokenizer(br.readLine());
            c[i] = Integer.parseInt(st.nextToken());
            d[i] = Integer.parseInt(st.nextToken());
        }
        Arrays.sort(a);
        int[] pos = new int[n];
        for (int i = 0; i < n; i++) {
            pos[i] = i;
        }
        Arrays.sort(pos, (x, y) -> a[x] - a[y]);
        SegmentTree stree = new SegmentTree(n);
        for (int i = 0; i < n; i++) {
            stree.update(1, 0, n - 1, pos[i], pos[i], b[pos[i]]);
        }
        int[] ans = new int[m];
        int cnt = 0;
        for (int i = 0; i < m; i++) {
            int l = Arrays.binarySearch(a, c[i]);
            int r = Arrays.binarySearch(a, d[i]);
            if (l < 0) l = -l - 1;
            if (r < 0) r = -r - 1;
            if (stree.query(1, 0, n - 1, l, r) == 0) {
                ans[cnt++] = i + 1;
            }
            stree.update(1, 0, n - 1, l, r, 1 - b[pos[l]]);
            stree.update(1, 0, n - 1, r, n - 1, 1 - b[pos[r]]);
        }
        if (cnt == 0) {
            System.out.println(-1);
        } else {
            StringBuilder sb = new StringBuilder();
            for (int i = 0; i < cnt; i++) {
                sb.append(ans[i]);
                if (i < cnt - 1) sb.append(" ");
            }
            System.out.println(sb.toString());
        }
    }
}
