import sys
input = sys.stdin.readline

class SegmentTree:
    def __init__(self, n):
        self.n = n
        self.tree = [0] * (4 * n)

    def update(self, node, start, end, index, value):
        if start == end:
            self.tree[node] = value
            return
        mid = (start + end) // 2
        if index <= mid:
            self.update(2 * node, start, mid, index, value)
        else:
            self.update(2 * node + 1, mid + 1, end, index, value)
        self.tree[node] = self.tree[2 * node] + self.tree[2 * node + 1]

    def query(self, node, start, end, left, right):
        if start > right or end < left:
            return 0
        if start >= left and end <= right:
            return self.tree[node]
        mid = (start + end) // 2
        return self.query(2 * node, start, mid, left, right) + self.query(2 * node + 1, mid + 1, end, left, right)

def solve():
    n, m = map(int, input().split())
    a = []
    b = []
    for _ in range(n):
        x, y = map(int, input().split())
        a.append(x)
        b.append(y)
    l = []
    r = []
    for _ in range(m):
        x, y = map(int, input().split())
        l.append(x)
        r.append(y)
    a.sort()
    b.sort()
    seg = SegmentTree(n)
    for i in range(n):
        seg.update(1, 0, n - 1, i, b[i])
    ans = []
    for i in range(m):
        left = l[i]
        right = r[i]
        index = 0
        while index < n and a[index] < left:
            index += 1
        while index < n and a[index] <= right:
            if seg.query(1, 0, n - 1, index, n - 1) == 0:
                ans.append(i + 1)
                break
            index += 1
    if len(ans) == 0:
        print(-1)
    else:
        print(len(ans))
        for i in sorted(ans):
            print(i, end=' ')

solve()
