#include <stdio.h>
#include <stdbool.h>

int main() {
    int n;
    scanf("%d", &n);

    bool distinct = true;
    int prev = -1;

    for (int i = 0; i < n; i++) {
        int num;
        scanf("%d", &num);

        if (num == prev) {
            distinct = false;
        }

        prev = num;
    }

    printf("%s\n", distinct ? "YES" : "NO");

    return 0;
}
