#include <iostream>
#include <unordered_set>

int main() {
    int n;
    std::cin >> n;
    std::unordered_set<int> s;
    for (int i = 0; i < n; i++) {
        int x;
        std::cin >> x;
        if (s.find(x) != s.end()) {
            std::cout << "NO" << std::endl;
            return 0;
        }
        s.insert(x);
    }
    std::cout << "YES" << std::endl;
    return 0;
}
