#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 200000
#define P 1000

double dp[N + 1][P + 1];

int main() {
    int n, k;
    int p[N];
    int i, j, l, r;

    scanf("%d %d", &n, &k);
    for (i = 0; i < n; i++) {
        scanf("%d", &p[i]);
    }

    for (i = 1; i <= n; i++) {
        for (j = 1; j <= p[i - 1]; j++) {
            dp[i][j] = (double)j / p[i - 1];
        }
    }

    for (i = 1; i <= n; i++) {
        for (j = 1; j <= p[i - 1]; j++) {
            for (l = 1; l <= i; l++) {
                for (r = 1; r <= p[i - l]; r++) {
                    dp[i][j] = fmax(dp[i][j], dp[i - l][j - r] + (double)(j + r) / p[i - 1]);
                }
            }
        }
    }

    printf("%.6lf\n", dp[n][k]);

    return 0;
}
