#include <iostream>
#include <string>
#include <vector>

using namespace std;

int countNumbers(int n, int k) {
    if (k > 3) return 0;
    if (k == 1) {
        int count = 0;
        for (int i = 1; i <= n; i++) {
            string s = to_string(i);
            if (s.length() == 1) count++;
            else if (s.length() == 2) count += 9;
            else if (s.length() == 3) count += 9 * 9;
            else if (s.length() == 4) count += 9 * 9 * 9;
            else if (s.length() == 5) count += 9 * 9 * 9 * 9;
            else if (s.length() == 6) count += 9 * 9 * 9 * 9 * 9;
            else if (s.length() == 7) count += 9 * 9 * 9 * 9 * 9 * 9;
            else if (s.length() == 8) count += 9 * 9 * 9 * 9 * 9 * 9 * 9;
            else if (s.length() == 9) count += 9 * 9 * 9 * 9 * 9 * 9 * 9 * 9;
        }
        return count;
    } else if (k == 2) {
        int count = 0;
        for (int i = 1; i <= n; i++) {
            string s = to_string(i);
            if (s.length() == 2) count += 9;
            else if (s.length() == 3) count += 9 * 9;
            else if (s.length() == 4) count += 9 * 9 * 9;
            else if (s.length() == 5) count += 9 * 9 * 9 * 9;
