import java.util.Scanner;

public class Main {
    static final int MAXN = 100;
    static final int MAXK = 3;

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String n = scanner.next();
        int k = scanner.nextInt();
        int ans = 0;

        for (int i = 1; i <= k; i++) {
            ans += count(n.length(), k, i);
        }

        for (int i = 1; i <= n.length() - k; i++) {
            ans += count(n.length(), k, n.length() - i);
        }

        System.out.println(ans);
    }

    static int count(int n, int k, int p) {
        int ans = 0;
        for (int i = 1; i <= p; i++) {
            int x = n - p + 1;
            if (x >= k) {
                ans += choose(x, k);
            }
        }
        return ans;
    }

    static int choose(int n, int k) {
        int ans = 1;
        for (int i = 1; i <= k; i++) {
            ans = ans * (n - i + 1) / i;
        }
        return ans;
    }
}
