#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

long long factorial(int n) {
    long long result = 1;
    for (int i = 1; i <= n; i++) {
        result = (result * i) % MOD;
    }
    return result;
}

long long combination(int n, int k) {
    return (factorial(n) * pow_mod(factorial(k), MOD - 2, MOD) % MOD * pow_mod(factorial(n - k), MOD - 2, MOD) % MOD) % MOD;
}

long long pow_mod(long long base, int exponent, int mod) {
    long long result = 1;
    while (exponent > 0) {
        if (exponent % 2 == 1) {
            result = (result * base) % mod;
        }
        base = (base * base) % mod;
        exponent /= 2;
    }
    return result;
}

int main() {
    int r1, c1, r2, c2;
    scanf("%d %d %d %d", &r1, &c1, &r2, &c2);

    long long sum = 0;
    for (int i = r1; i <= r2; i++) {
        for (int j = c1; j <= c2; j++) {
            sum = (sum + combination(i + j, i)) % MOD;
        }
    }

    printf("%lld\n", sum);
    return 0;
}
