#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

const int MOD = 1e9 + 7;

int main() {
    int r1, c1, r2, c2;
    std::cin >> r1 >> c1 >> r2 >> c2;

    std::vector<std::vector<long long>> dp(r2 + 1, std::vector<long long>(c2 + 1));
    dp[0][0] = 1;

    for (int i = 0; i <= r2; ++i) {
        for (int j = 0; j <= c2; ++j) {
            if (i > 0) {
                dp[i][j] = (dp[i][j] + dp[i - 1][j]) % MOD;
            }
            if (j > 0) {
                dp[i][j] = (dp[i][j] + dp[i][j - 1]) % MOD;
            }
        }
    }

    long long ans = 0;
    for (int i = r1; i <= r2; ++i) {
        for (int j = c1; j <= c2; ++j) {
            ans = (ans + dp[i][j]) % MOD;
        }
    }

    std::cout << ans << std::endl;

    return 0;
}
