import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int H = scanner.nextInt();
        int A = scanner.nextInt();
        int attacks = 0;
        while (H > 0) {
            H -= A;
            attacks++;
        }
        System.out.println(attacks);
    }
}
