#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define MAX_N 200005

int main() {
    int n, k;
    scanf("%d %d", &n, &k);

    int h[MAX_N];
    for (int i = 0; i < n; i++) {
        scanf("%d", &h[i]);
    }

    long long sum = 0;
    for (int i = 0; i < n; i++) {
        sum += h[i];
    }

    if (k == 0) {
        long long max = 0;
        for (int i = 0; i < n; i++) {
            max = (max > h[i]) ? max : h[i];
        }
        printf("%lld\n", sum - max);
    } else {
        long long max = 0;
        for (int i = 0; i < n; i++) {
            max = (max > h[i]) ? max : h[i];
        }

        long long min = LLONG_MAX;
        for (int i = 0; i < n; i++) {
            if (h[i] < max) {
                min = (min < h[i]) ? min : h[i];
            }
        }

        long long ans = sum - max;
        if (min != 0) {
            ans += (max - min) * k;
        }
        printf("%lld\n", ans);
    }

    return 0;
}
