#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int a;
    int b;
} Spell;

int compare(const void *a, const void *b) {
    Spell *x = (Spell *)a;
    Spell *y = (Spell *)b;
    return (y->a - x->a);
}

int main() {
    int H, N;
    scanf("%d %d", &H, &N);
    Spell *spells = (Spell *)malloc(N * sizeof(Spell));
    for (int i = 0; i < N; i++) {
        scanf("%d %d", &spells[i].a, &spells[i].b);
    }
    qsort(spells, N, sizeof(Spell), compare);
    int i = 0;
    int total = 0;
    while (H > 0) {
        int min = spells[i].a;
        int min_index = i;
        for (int j = i; j < N; j++) {
            if (spells[j].a <= min) {
                min = spells[j].a;
                min_index = j;
            }
        }
        int count = H / min;
        total += count * spells[min_index].b;
        H -= count * min;
        i = min_index;
    }
    printf("%d\n", total);
    free(spells);
    return 0;
}
