#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int H, N;
    cin >> H >> N;

    vector<pair<int, int>> spells(N);
    for (int i = 0; i < N; i++) {
        cin >> spells[i].first >> spells[i].second;
    }

    sort(spells.begin(), spells.end(), [](const pair<int, int>& a, const pair<int, int>& b) {
        return a.first * 1.0 / a.second > b.first * 1.0 / b.second;
    });

    int total = 0;
    int health = H;
    for (const auto& spell : spells) {
        int count = health / spell.first;
        total += count * spell.second;
        health -= count * spell.first;
        if (health <= 0) break;
    }

    if (health > 0) {
        total += health * spells[0].second;
    }

    cout << total << endl;

    return 0;
}
