import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int H = scanner.nextInt();
        int N = scanner.nextInt();
        int[][] spells = new int[N][2];

        for (int i = 0; i < N; i++) {
            spells[i][0] = scanner.nextInt();
            spells[i][1] = scanner.nextInt();
        }

        Arrays.sort(spells, (a, b) -> b[0] - a[0]);

        int magicPoints = 0;
        int remainingHealth = H;

        for (int i = 0; i < N; i++) {
            int spellCost = spells[i][1];
            int spellEffect = spells[i][0];

            int numSpells = remainingHealth / spellEffect;
            magicPoints += numSpells * spellCost;
            remainingHealth -= numSpells * spellEffect;

            if (remainingHealth <= 0) {
                break;
            }
        }

        System.out.println(magicPoints);
    }
}
