#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 200005

typedef struct {
    int x, h;
} Monster;

int cmp(const void *a, const void *b) {
    return ((Monster *)a)->x - ((Monster *)b)->x;
}

int main() {
    int n, d, a;
    scanf("%d%d%d", &n, &d, &a);
    Monster m[N];
    for (int i = 0; i < n; i++) {
        scanf("%d%d", &m[i].x, &m[i].h);
    }
    qsort(m, n, sizeof(Monster), cmp);
    long long ans = 0;
    int i = 0;
    while (i < n) {
        int j = i;
        while (j < n && m[j].x <= m[i].x + d) {
            m[j].h -= a;
            j++;
        }
        while (i < j) {
            if (m[i].h <= 0) {
                i++;
            } else {
                long long k = (long long)(m[i].h + a - 1) / a;
                ans += k;
                m[i].h -= k * a;
                i++;
            }
        }
    }
    printf("%lld\n", ans);
    return 0;
}
