#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Monster {
    int x, h;
};

bool compareMonsters(const Monster& a, const Monster& b) {
    return a.x < b.x;
}

int main() {
    int n, d, a;
    cin >> n >> d >> a;

    vector<Monster> monsters(n);
    for (int i = 0; i < n; i++) {
        cin >> monsters[i].x >> monsters[i].h;
    }

    sort(monsters.begin(), monsters.end(), compareMonsters);

    long long ans = 0;
    int i = 0;
    while (i < n) {
        int j = i;
        while (j < n && monsters[j].x <= monsters[i].x + d) {
            monsters[j].h -= a;
            j++;
        }
        while (i < j) {
            if (monsters[i].h <= 0) {
                i++;
            } else {
                int k = i;
                while (k < j && monsters[k].h > 0) {
                    k++;
                }
                if (k == j) {
                    ans += (monsters[j].h + a - 1) / a;
                    break;
                }
                ans += (monsters[k - 1].h + a - 1) / a;
                i = k;
                break;
            }
        }
    }

    cout << ans << endl;

    return 0;
}
