import java.io.*;
import java.util.*;

public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }
    }

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        int n = sc.nextInt();
        long d = sc.nextLong();
        long a = sc.nextLong();
        long[] x = new long[n];
        long[] h = new long[n];
        for (int i = 0; i < n; i++) {
            x[i] = sc.nextLong();
            h[i] = sc.nextLong();
        }
        long[] pre = new long[n + 1];
        for (int i = 0; i < n; i++) {
            pre[i + 1] = pre[i] + h[i];
        }
        long ans = 0;
        for (int i = 0; i < n; i++) {
            long l = x[i] - d;
            long r = x[i] + d;
            int left = Arrays.binarySearch(x, 0, n, l);
            if (left < 0) left = -left - 1;
            int right = Arrays.binarySearch(x, 0, n, r);
            if (right < 0) right = -right - 2;
            long sum = pre[right + 1] - pre[left];
            long need = (sum + a - 1) / a;
            ans += need;
        }
        System.out.println(ans);
    }
}
