import sys
input = sys.stdin.readline

N, D, A = map(int, input().split())
X = [0] + list(map(int, input().split()))[::2]
H = [0] + list(map(int, input().split()))[::2]
X.sort()

ans = 0
for i in range(1, N + 1):
    if X[i] - X[i - 1] > D:
        l, r = X[i - 1] + D, X[i] - D
        while l < r:
            m = (l + r + 1) // 2
            if m - X[i - 1] <= D:
                l = m
            else:
                r = m - 1
        ans += (l - X[i - 1]) // D + 1
    else:
        l, r = X[i] - D, X[i] + D
        while l < r:
            m = (l + r + 1) // 2
            if m - X[i] <= D:
                l = m
            else:
                r = m - 1
        if l <= X[i - 1] + D:
            ans += (X[i] - X[i - 1]) // (2 * D) + 1
        else:
            ans += (X[i] - X[i - 1]) // (2 * D) + (l - X[i - 1]) // D + 1
    if H[i] <= 0:
        break
print(ans)
