def count_pairs(n):
    count = 0
    for i in range(1, n + 1):
        for j in range(1, n + 1):
            if i < 10 and j < 10:
                if i % 10 == j // 10 and j % 10 == i // 10:
                    count += 1
            else:
                if i % 10 == j // 10 and j % 10 == i // 10:
                    count += 1
    return count

