#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define N 55
#define M 25

int n, m;
int a[N], b[N], u[M], v[M];
int fa[N], sz[N], son[N], top[N], dfn[N], dep[N], id[N], st[N], ed[N];
int nst, ned, cnt, ans;
int f[N][1 << 20];

void dfs(int x, int fath) {
    fa[x] = fath;
    sz[x] = 1;
    dep[x] = dep[fath] + 1;
    id[x] = ++cnt;
    st[x] = ++nst;
    dfn[x] = nst;
    for (int i = 1; i <= m; i++) {
        if (u[i] == x) v[i] = fath;
        else if (v[i] == x) u[i] = fath;
    }
    for (int i = 1; i <= n; i++) {
        if (i != fath && a[i] == x) {
            son[x] = i;
            dfs(i, x);
            sz[x] += sz[i];
        }
    }
    ed[x] = ++ned;
    dfn[x] = nst;
}

int lca(int x, int y) {
    while (top[x] != top[y]) {
        if (dep[top[x]] < dep[top[y]]) std::swap(x, y);
        x = fa[top[x]];
    }
    return dep[x] < dep[y] ? x : y;
}

int query(int x, int y) {
    int l = lca(x, y);
    return (dep[x] + dep[y] - 2 * dep[l]) / 2;
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i <= n; i++) {
        scanf("%d%d", &a[i], &b[i]);
    }
    scanf("%d", &m);
    for (int i = 1; i <= m; i++) {
        scanf("%d%d", &u[i], &v[i]);
    }
    dfs(1, 0);
    for (int i = 1; i <= n; i++) {
        top[i] = i;
        while (top[i] && son[top[i]] == i) top[i] = fa[top[i]];
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 0; j < (1 << m); j++) {
            f[i][j] = 0;
        }
    }
    f[1][0] = 1;
    for (int i = 1; i <= n; i++) {
        for (int j = 0; j < (1 << m); j++) {
            if (f[i][j]) {
                for (int k = 1; k <= m; k++) {
                    if ((j >> (k - 1)) & 1) continue;
                    int x = u[k], y = v[k];
                    if (x == i || y == i) continue;
                    int l = lca(x, y);
                    if (l == i) continue;
                    int p = query(x, y);
                    if (p < ed[i]) f[son[i]][j | (1 << (k - 1))] |= f[i][j];
                }
            }
        }
    }
    ans = 0;
    for (int i = 0; i < (1 << m); i++) {
        if (f[n][i]) ans++;
    }
    printf("%d\n", ans);
    return 0;
}
