#include <iostream>
#include <vector>
#include <bitset>
#include <algorithm>

using namespace std;

const int N = 55;

int n, m;
vector<int> g[N];
int u[N], v[N];
int p[N][N];

void dfs(int x, int fa) {
    for (int y : g[x]) {
        if (y == fa) continue;
        dfs(y, x);
        p[x][y] = p[y][x] = ++p[0][0];
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n;
    for (int i = 1; i < n; i++) {
        int x, y;
        cin >> x >> y;
        g[x].push_back(y);
        g[y].push_back(x);
    }

    dfs(1, 0);

    cin >> m;
    for (int i = 1; i <= m; i++) {
        cin >> u[i] >> v[i];
        p[u[i]][v[i]] = p[v[i]][u[i]] = 0;
    }

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            p[i][j] = max(p[i][j], p[i][0] + p[0][j]);
        }
    }

    bitset<N> f;
    f[0] = 1;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            if (p[i][j]) {
                f |= f << p[i][j];
            }
        }
    }

    int ans = 0;
    for (int i = 1; i <= m; i++) {
        f &= f << p[u[i]][v[i]];
    }

    cout << f.count() << '\n';

    return 0;
}
