import java.io.*;
import java.util.*;

public class Main {
    static int N, M;
    static int[][] edges;
    static int[][] restrictions;
    static int[][] parent;
    static int[] size;
    static int[] dp;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        N = Integer.parseInt(st.nextToken());
        edges = new int[N][2];
        for (int i = 0; i < N - 1; i++) {
            st = new StringTokenizer(br.readLine());
            edges[i][0] = Integer.parseInt(st.nextToken()) - 1;
            edges[i][1] = Integer.parseInt(st.nextToken()) - 1;
        }

        M = Integer.parseInt(br.readLine());
        restrictions = new int[M][2];
        for (int i = 0; i < M; i++) {
            st = new StringTokenizer(br.readLine());
            restrictions[i][0] = Integer.parseInt(st.nextToken()) - 1;
            restrictions[i][1] = Integer.parseInt(st.nextToken()) - 1;
        }

        parent = new int[N][20];
        size = new int[N];
        for (int i = 0; i < N; i++) {
            parent[i][0] = i;
            size[i] = 1;
        }

        for (int i = 0; i < 19; i++) {
            for (int j = 0; j < N; j++) {
                if (parent[j][i] != j) {
                    parent[j][i + 1] = parent[parent[j][i]][i];
                }
            }
        }

        dp = new int[1 << (N - 1)];
        Arrays.fill(dp, -1);
        pw.println(dfs(0, 0));
        pw.close();
    }

    static int dfs(int node, int mask) {
        if (mask == (1 << (N - 1)) - 1) {
            return 1;
        }

        if (dp[mask] != -1) {
            return dp[mask];
        }

        int res = 0;
        for (int i = 0; i < N - 1; i++) {
            if ((mask & (1 << i)) == 0) {
                int next = mask | (1 << i);
                int u = edges[i][0];
                int v = edges[i][1];
                int lca = lca(u, v, mask);
                if (lca == u) {
                    lca = v;
                }
                if (lca != u) {
                    res += dfs(node, next);
                }
            }
        }
        return dp[mask] = res;
    }

    static int lca(int u, int v, int mask) {
        if (u == v) {
            return u;
        }

        for (int i = 19; i >= 0; i--) {
            if (parent[u][i] != parent[v][i]) {
                u = parent[u][i];
                v = parent[v][i];
            }
        }

        return parent[u][0];
    }
}
