class LCA:
    def __init__(self, n):
        self.n = n
        self.log = [0] * (n + 1)
        self.log[1] = 0
        for i in range(2, n + 1):
            self.log[i] = self.log[i // 2] + 1
        self.dep = [0] * (n + 1)
        self.par = [[0] * (self.log[n] + 1) for _ in range(n + 1)]
        self.up = [[0] * (self.log[n] + 1) for _ in range(n + 1)]

    def dfs(self, u, p, dep):
        self.dep[u] = dep
        self.par[u][0] = p
        for i in range(1, self.log[self.n] + 1):
            if dep - (1 << i) >= 0:
                self.par[u][i] = self.par[self.par[u][i - 1]][i - 1]
        self.up[u][0] = p
        for i in range(1, self.log[self.n] + 1):
            if self.par[u][i - 1] != 0:
                self.up[u][i] = self.up[self.up[u][i - 1]][i - 1]
        for v in self.g[u]:
            if v != p:
                self.dfs(v, u, dep + 1)

    def lca(self, u, v):
        if self.dep[u] < self.dep[v]:
            u, v = v, u
        for i in range(self.log[self.n], -1, -1):
            if self.dep[self.par[u][i]] >= self.dep[v]:
                u = self.par[u][i]
        if u == v:
            return u
        for i in range(self.log[self.n], -1, -1):
            if self.par[u][i] != self.par[v][i]:
                u = self.par[u][i]
                v = self.par[v][i]
        return self.par[u][0]

    def kth(self, u, k):
        for i in range(self.log[self.n], -1, -1):
            if k - (1 << i) >= 0:
                u = self.up[u][i]
        return u

def main():
