#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007
#define MAXN 2000

int cmp(const void *a, const void *b) {
    return (*(int*)a - *(int*)b);
}

int main() {
    int N, K;
    scanf("%d%d", &N, &K);
    int A[MAXN];
    for (int i = 0; i < N; i++) {
        scanf("%d", &A[i]);
    }
    qsort(A, N, sizeof(int), cmp);
    int inv = 0;
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < i; j++) {
            inv = (inv + (long long)K * (K - 1) / 2 + (long long)K * (K - 1) / 2 * (i - j) + (long long)K * (K - 1) / 2 * (N - i) * (N - j)) % MOD;
        }
        int cnt = 0;
        for (int j = 0; j < N; j++) {
            if (A[i] > A[j]) {
                cnt++;
            }
        }
        inv = (inv + (long long)cnt * K) % MOD;
    }
    printf("%d\n", inv);
    return 0;
}
