import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static int n, k;
    static int[] a, b, c;
    static int[] tmp;
    static int[] inv;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        n = Integer.parseInt(st.nextToken());
        k = Integer.parseInt(st.nextToken());
        a = new int[n];
        b = new int[n];
        c = new int[n];
        tmp = new int[n];
        inv = new int[n];

        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < n; i++) {
            a[i] = Integer.parseInt(st.nextToken());
            b[i] = a[i];
            c[i] = a[i];
        }

        Arrays.sort(b);
        int m = 0;
        for (int i = 0; i < n; i++) {
            if (b[i] != b[m]) {
                m++;
                b[m] = b[i];
            }
        }

        for (int i = 0; i < n; i++) {
            inv[i] = pow(b[i], MOD - 2);
        }

        for (int i = 0; i < n; i++) {
            tmp[i] = Arrays.binarySearch(b, 1, m + 1, a[i]);
            if (tmp[i] < 0) tmp[i] = -tmp[i] - 1;
        }

        int ans = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < i; j++) {
                if (a[i] > a[j]) {
                    ans = (ans + (long) k * (k - 1) / 2 * (tmp[i] - tmp[j])) % MOD;
                }
            }
            for (int j = 0; j < n; j++) {
                if (a[i] > c[j]) {
                    ans = (ans + (long) k * (tmp[i] - (j + 1))) % MOD;
                }
            }
        }

        pw.println(ans);
        pw.close();
    }

    static int pow(int a, int b) {
        int res = 1;
        while (b > 0) {
            if ((b & 1) == 1) res = (int) ((long) res * a % MOD);
            a = (int) ((long) a * a % MOD);
            b >>= 1;
        }
        return res;
    }
}
