import sys
input = sys.stdin.readline

mod = 10**9 + 7

def add(a, b):
    return (a + b) % mod

def sub(a, b):
    return (a - b + mod) % mod

def mul(a, b):
    return (a * b) % mod

def inv(a):
    return pow(a, mod - 2, mod)

def comb(n, k):
    if k > n: return 0
    ans = 1
    for i in range(1, k + 1):
        ans = mul(ans, n - i + 1)
        ans = mul(ans, inv(i))
    return ans

def solve():
    n, k = map(int, input().split())
    a = list(map(int, input().split()))
    m = max(a)
    cnt = [0] * (m + 1)
    for i in a:
        cnt[i] += 1
    ans = 0
    for i in range(1, m + 1):
        ans = add(ans, mul(comb(n * k, cnt[i]), mul(cnt[i], (n * k - cnt[i]))))
        for j in range(i + 1, m + 1):
            ans = add(ans, mul(comb(n * k, cnt[i]), mul(cnt[j], (n * k - cnt[j]))))
            ans = add(ans, mul(comb(n * k, cnt[i]), mul(cnt[j], (n * k - cnt[j]))))
            ans = add(ans, mul(comb(n * k, cnt[i]), mul(cnt[j], (n * k - cnt[j]))))
    print(ans)

t = 1
for _ in range(t):
    solve()
