#include <iostream>
#include <string>
#include <vector>

using namespace std;

const int MOD = 1e9 + 7;

int main() {
    int n;
    string s;
    cin >> n >> s;

    int cnt = 0;
    for (char c : s) {
        if (c == 'B') cnt++;
    }

    if (cnt % 2 != 0) {
        cout << 0 << endl;
        return 0;
    }

    vector<int> dp(2 * n + 1);
    dp[0] = 1;
    for (int i = 0; i < 2 * n; i++) {
        if (s[i] == 'B') {
            dp[i + 1] = (dp[i + 1] + dp[i]) % MOD;
            if (i > 0 && s[i - 1] == 'B') {
                dp[i + 2] = (dp[i + 2] + dp[i]) % MOD;
            }
        } else {
            dp[i + 1] = (dp[i + 1] + dp[i]) % MOD;
            if (i > 0 && s[i - 1] == 'W') {
                dp[i + 2] = (dp[i + 2] + dp[i]) % MOD;
            }
        }
    }

    cout << dp[2 * n] << endl;

    return 0;
}
